import React, { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import socket from "../socket";
import CodeEditor from "../components/CodeEditor";

function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const username =
    location.state?.username || `guest-${Math.floor(Math.random() * 1000)}`;

  const [players, setPlayers] = useState([]);
  const [playerEditors, setPlayerEditors] = useState({}); 
  const [scores, setScores] = useState({});
  const [matchStarted, setMatchStarted] = useState(false);

  useEffect(() => {
    socket.emit("join-room", { roomId, username }, (resp) => {
      if (!resp || !resp.ok) {
        alert("Failed to join room: " + (resp?.error || "unknown"));
        navigate("/");
      }
    });

    socket.on("player-list", (list) => {
      setPlayers(list);

      setPlayerEditors((prev) => {
        const updated = { ...prev };
        list.forEach((p) => {
          if (!updated[p.socketId]) {
            updated[p.socketId] = "// waiting for code...";
          }
        });
        return updated;
      });

      setScores((prev) => {
        const updated = { ...prev };
        list.forEach((p) => {
          if (updated[p.socketId] == null) {
            updated[p.socketId] = 0;
          }
        });
        return updated;
      });
    });

    socket.on("all-codes", (codes) => {
      setPlayerEditors(codes);
    });

    socket.on("match-started", () => {
      setMatchStarted(true);
    });

    socket.on("match-stopped", () => {
      setMatchStarted(false);
    });

    socket.on("score-update", (updatedScores) => {
      setScores(updatedScores);
    });

    return () => {
      socket.emit("leave-room", { roomId });
      socket.off("player-list");
      socket.off("all-codes");
      socket.off("match-started");
      socket.off("match-stopped");
      socket.off("score-update");
    };
  }, [roomId, username, navigate]);

  const handleCodeChange = (playerId, value) => {
    setPlayerEditors((prev) => ({
      ...prev,
      [playerId]: value,
    }));
    if (playerId === socket.id) {
      socket.emit("code-change", { roomId, code: value });
    }
  };

  const startMatch = () => {
    socket.emit("start-match", { roomId });
  };

  const stopMatch = () => {
    socket.emit("stop-match", { roomId });
  };

  const runCode = () => {
    const code = playerEditors[socket.id];
    socket.emit("run-code", { roomId, code });
  };

  return (
    <div style={{ padding: "20px", height: "100vh", boxSizing: "border-box" }}>
      <h2>Room: {roomId}</h2>
      <p>Logged in as: {username}</p>

      <div style={{ marginBottom: "10px" }}>
        {!matchStarted ? (
          <button onClick={startMatch}>Start Match</button>
        ) : (
          <button onClick={stopMatch}>Stop Match</button>
        )}
      </div>

      <div style={{ marginBottom: "20px" }}>
        <h3>Scores</h3>
        <ul>
          {players.map((p) => (
            <li key={p.socketId}>
              {p.username} {p.socketId === socket.id ? "(You)" : ""} :{" "}
              {scores[p.socketId] ?? 0}
            </li>
          ))}
        </ul>
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gridTemplateRows: "1fr 1fr",
          gap: "10px",
          height: "85vh",
        }}
      >
        {Array.from({ length: 4 }).map((_, idx) => {
          const player = players[idx];
          return (
            <div
              key={player?.socketId || idx}
              style={{
                border: "1px solid #ccc",
                display: "flex",
                flexDirection: "column",
                minHeight: 0,
              }}
            >
              <div
                style={{
                  background: "#eee",
                  padding: "5px",
                  flexShrink: 0,
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <span>
                  {player
                    ? `${player.username}${
                        player.socketId === socket.id ? " (You)" : ""
                      }`
                    : `Waiting for player ${idx + 1}...`}
                </span>
                {/* Show Run button only for this player's own playground */}
                {player && player.socketId === socket.id && (
                  <button onClick={runCode}>Run Code</button>
                )}
              </div>

              <div
                style={{
                  flex: 1,
                  minHeight: 0,
                  overflow: "hidden",
                }}
              >
                {player && (
                  <CodeEditor
                    code={playerEditors[player.socketId] || ""}
                    onChange={(value) =>
                      handleCodeChange(player.socketId, value)
                    }
                    readOnly={player.socketId !== socket.id}
                    style={{ width: "100%", height: "100%" }}
                  />
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Room;
