// src/pages/Room.js
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import socket from "../socket";
import CodeEditor from "../components/CodeEditor";

function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const username =
    location.state?.username || `guest-${Math.floor(Math.random() * 1000)}`;

  const [players, setPlayers] = useState([]);
  const [playerEditors, setPlayerEditors] = useState({}); // { socketId: code }

  useEffect(() => {
    // Join the room
    socket.emit("join-room", { roomId, username }, (resp) => {
      if (!resp || !resp.ok) {
        alert("Failed to join room: " + (resp?.error || "unknown"));
        navigate("/");
      }
    });

    socket.on("player-list", (list) => {
      setPlayers(list);
      setPlayerEditors((prev) => {
        const updated = { ...prev };
        list.forEach((p) => {
          if (!updated[p.socketId]) {
            updated[p.socketId] = "// waiting for code...";
          }
        });
        return updated;
      });
    });

    // Listen for code updates for all players
    socket.on("all-codes", (codes) => {
      setPlayerEditors(codes);
    });

    return () => {
      socket.emit("leave-room", { roomId });
      socket.off("player-list");
      socket.off("all-codes");
    };
  }, [roomId, username, navigate]);

  const handleCodeChange = (playerId, value) => {
    setPlayerEditors((prev) => ({
      ...prev,
      [playerId]: value,
    }));
    socket.emit("code-change", { roomId, playerId, code: value });
  };

  return (
    <div style={{ padding: "20px", height: "100vh", boxSizing: "border-box" }}>
      <h2>Room: {roomId}</h2>
      <p>Logged in as: {username}</p>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gridTemplateRows: "1fr 1fr",
          gap: "10px",
          height: "85%",
        }}
      >
        {Array.from({ length: 4 }).map((_, idx) => {
          const player = players[idx];
          return (
            <div
              key={idx}
              style={{
                border: "1px solid #ccc",
                display: "flex",
                flexDirection: "column",
                background: "#fff",
                overflow: "hidden",
              }}
            >
              <div style={{ background: "#eee", padding: "5px" }}>
                {player
                  ? `${player.username}${
                      player.socketId === socket.id ? " (You)" : ""
                    }`
                  : "Waiting for player..."}
              </div>
              <div style={{ flex: 1 }}>
                <CodeEditor
                  code={
                    player ? playerEditors[player.socketId] || "" : ""
                  }
                  onChange={(value) =>
                    player && handleCodeChange(player.socketId, value)
                  }
                  readOnly={!player || player.socketId !== socket.id}
                />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Room;
