// src/pages/Room.js
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import socket from "../socket";
import CodeEditor from "../components/CodeEditor";

function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const username =
    location.state?.username || `guest-${Math.floor(Math.random() * 1000)}`;

  const [players, setPlayers] = useState([]);
  const [playerEditors, setPlayerEditors] = useState({}); // { playerId: code }

  useEffect(() => {
    socket.emit("join-room", { roomId, username }, (resp) => {
      if (!resp || !resp.ok) {
        alert("Failed to join room: " + (resp?.error || "unknown"));
        navigate("/");
      }
    });

    socket.on("player-list", (list) => {
      setPlayers(list);
      setPlayerEditors((prev) => {
        const updated = { ...prev };
        list.forEach((p) => {
          if (!updated[p.socketId]) {
            updated[p.socketId] = "// waiting for code...";
          }
        });
        return updated;
      });
    });

    socket.on("all-codes", (codes) => {
      setPlayerEditors(codes);
    });

    return () => {
      socket.emit("leave-room", { roomId });
      socket.off("player-list");
      socket.off("all-codes");
    };
  }, [roomId, username, navigate]);

  const handleCodeChange = (playerId, value) => {
    setPlayerEditors((prev) => ({
      ...prev,
      [playerId]: value,
    }));
    socket.emit("code-change", { roomId, code: value });
  };

  return (
    <div style={{ padding: "20px", height: "100vh", boxSizing: "border-box" }}>
      <h2>Room: {roomId}</h2>
      <p>Logged in as: {username}</p>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gridTemplateRows: "1fr 1fr",
          gap: "10px",
          height: "85vh",
        }}
      >
        {Array.from({ length: 4 }).map((_, idx) => {
          const player = players[idx];
          return (
            <div
              key={player ? player.socketId : idx}
              style={{
                border: "1px solid #ccc",
                display: "flex",
                flexDirection: "column",
                height: "100%", // Ensures same size in grid
              }}
            >
              {player ? (
                <>
                  <div style={{ background: "#eee", padding: "5px" }}>
                    {player.username}
                    {player.socketId === socket.id && " (You)"}
                  </div>
                  <div style={{ flex: 1, overflow: "hidden" }}>
                    <CodeEditor
                      code={playerEditors[player.socketId] || ""}
                      onChange={(value) =>
                        handleCodeChange(player.socketId, value)
                      }
                      style={{ height: "100%", width: "100%" }} // Ensures full stretch
                    />
                  </div>
                </>
              ) : (
                <div
                  style={{
                    flex: 1,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    background: "#f9f9f9",
                    height: "100%",
                  }}
                >
                  Waiting for player...
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Room;
